    function checkCKloaded()
    {
        if (typeof CKEDITOR == "object") 
		{
			var _editorInstances = CKEDITOR.instances;
			instancesCount =0;
			for (instanceName in CKEDITOR.instances)
            {
                instancesCount++;
            }
            if (instancesCount != fck_loadCompleteCount)
            {

                alert("" );

                return false;
            }
        }
        return true;
    }

