tr.td:
           
           
               var img1 = new Image(10,11);
               img1.src = "Variables.cs_imagedir/graphics/common/controlpanel/minus.gif";
               var img2 = new Image(10,11);
               img2.src = "Variables.cs_imagedir/graphics/common/controlpanel/plus.gif";
               var currentImage = img2;
               function AssigneeTableSwap() {
                 var obj = document.forms[0].elements[0];
                 var thisStyleObj = document.getElementById("AssigneeTable");
                 if (currentImage == img1)
                 {
                   document.images['AssigneeExpander'].src = img2.src;
                   currentImage = img2;
                   thisStyleObj.style.display='none';

                 } else {
                     document.images['AssigneeExpander'].src = img1.src;
                     currentImage = img1;
                     thisStyleObj.style.display='block';
                 }
              }
           
           
            
           
           
