function ReloadContentWindow()
{
    
    var fixedUrl;
    var url = parent.location.href;
    var i1 = url.indexOf('rendermode=');
    if (i1==-1) {
        fixedUrl = url + "&rendermode=preview";
    } else {
        var i2 = url.indexOf('&', i1);
        if (i2==-1) {
            fixedUrl = url.substring(0,i1) + "rendermode=preview";
        } else {
            fixedUrl = url.substring(0,i1) + "rendermode=preview" + url.substring(i2);
        }
    }

    parent.location=fixedUrl;
    return;
    
}



   
        



        <script language="JavaScript">
            parent.location.reload(true);
