REPLACEALL.SCRIPT:
if (top != self) {	
	var wemContext = WemContext.getInstance();
	wemContext.contextPath	='Variables.path';
	wemContext.directToLoginPage();
}
else
{
	var _wemEnabled = true;
	
	if(typeof(WemContext) === 'undefined'){
		_wemEnabled = false;
	}
	
	var appendString = _wemEnabled ? 'Variables.path' + 'wem/fatwire/home' : 'Variables.cs_imagedir/LoginPage.html';
	var href = top.location.href;
	top.location.href = href.split('/').slice(0,3).join('/') + appendString;
}
