	function setAddToAL(obj,id) {}
	
	function AddToAL()
	{ var i=0;
		var obj = document.forms[0];
		for (i=0;  i < obj.elements.length;i++) {
			if (obj.elements[i].name=="AddALAsset") {
				if (obj.elements[i].checked==true) {
					obj.pagename.value='OpenMarket/Xcelerate/Actions/AddToActiveListPost';
					return true;
					} 
				}
		}
		alert("");
		return false;
	}
