function clearcurrCookie(name) {
	var dc = document.cookie;
	var prefix = "__" + name + "__" + "=";
	var begin = dc.indexOf("; " + prefix);
	if (begin == -1) {
		begin = dc.indexOf(prefix);
		if (begin != 0) { return "";}
	} else
	return "1";
}
					
function clearCookies(cat) {
	var currCookie;
	currCookie = clearcurrCookie(cat);
	var curCookie = "__" + cat + "__=" + "";
	if (currCookie != "") document.cookie = curCookie;
	}
					






	
		clearCookies("");
