FTCS.Script:
var nav4 = window.Event ? true : false

function setRelatedId(e, type, id) {

	if (nav4)
        ctrlKey = e.modifiers & Event.CONTROL_MASK;
    else
        ctrlKey = window.event.ctrlKey;

    if (ctrlKey)
    {
		if (type == "Image")
		{
			dest = parent.frames[0].document.forms[0].currImage.value;
			if (dest == "imageid1")
		 		parent.frames[0].document.forms[0].imageid1.value=id;
			else if (dest == "imageid2")
		 		parent.frames[0].document.forms[0].imageid2.value=id;
			else if (dest == "imageid3")
		 		parent.frames[0].document.forms[0].imageid3.value=id;		
		}
		if (type == "Linkset")
		{
			dest = parent.frames[0].document.forms[0].currLinkset.value;
			if (dest == "relatedid1")
		 		parent.frames[0].document.forms[0].relatedid1.value=id;
			else if (dest == "relatedid2")
		 		parent.frames[0].document.forms[0].relatedid2.value=id;
		}	
	}
	return true;
}

function writeCopyID(type, id)
{
        if (top.location!=self.location) {
            button = '<input type=button value=\"Copy ID\" onClick=\"doCopyID(\'' + type + '\', \'' + id + '\')\">';
            document.write(button);
        }
}


