FTCS.SCRIPT:
function checkExportParams(form)
{
	if (!isCatalogNameEntered(form))
		return false;

	if ( form.ftchoice[0].checked )
		return true;
	else if ( form.ftchoice[1].checked )
		form.tablekeyvalue.value = "";
	else
	{
		if (form.tablekeyvalue.value == "")
		{
			alert('');
			return false;
		}
	}

	return true;
}

function isCatalogNameEntered(form)
{
    if(form.tablename.value == "")
	{
		alert('');
		return false;
    }
	return true;
}
