FTCS.SCRIPT:
function areKeysEntered(form)
{
    if ( (form.table.value == "") )
        form.table.value="%";
    if ( (form.asset.value == "") )
        form.asset.value="%";

    return true;
}

function selectAll(form,select,checkbox)
{
    for(i=0; i < form.elements.length; i++)
	{
        obj = form.elements[i];
        if(obj.name.indexOf(checkbox) == 0)
			obj.checked = select;
	}
}

function verifyTableListParams(form,checkbox,noun)
{
	if (form.numversions.value=="")
	{
		alert('');

		return false;
	}
	if (form.numversions.value=="0")
	{
		alert('');
		return false;
	}
    for(i=0; i < form.elements.length; i++)
	{
        obj = form.elements[i];
        if(obj.name.indexOf(checkbox) == 0)
        {
			if (obj.checked)
			    return true;
		}
	}
	alert('');
	return false;
}
