FTCS.Script:
function gotoSearch(F, dict)
{
    
        
        
    
    
    var pURL = "Variables.urlsimpsearchfront";
    
    i = 0;
    
    while (i < dict.options.length) {
        if (dict.options[i].selected) {
            var value = dict.options[i].value;

            if (value == "*") return;

            pURL = pURL + "&AssetType="+dict.options[i].value;
            break;
        }
        i++;
    }
    parent.frames['XcelAction'].location=pURL;
    
}
