FTCS.Script:
    function gotoTasks(F, dict)
    {
    
        
        
    
    
        
        
    
    
        
        
    
    
        
        
    
        
        var pURL= "";
        var pURL1 = "Variables.urlworklistfront";
        var pURL2 = "Variables.urlcheckoutfront";
        var pURL3 = "Variables.urlrecentfront";
        var pURL4 = "Variables.urlwfgrpfront";
        
    
        i = 0;
        doNothing=false;
        while (i < dict.options.length) {
            if (dict.options[i].selected) {
                var value = dict.options[i].value;
    
                if (value == "ShowTasks") {
                    pURL = pURL1;                
                }
		    else if (value == "ShowCheckouts") {
                    pURL = pURL2;
                }
                else if (value == "*") {
                    doNothing=true;
                }
                else if (value == "ShowRecentFront") {
                	pURL = pURL3;
                	}
                else if (value == "AddNew") {
                	pURL = pURL4 + "&action=new";
				pURL = pURL + "&pagetype=front";
                	}
                else {
                    pURL = pURL4 + "&name=";
		    		pURL = pURL + value;
					pURL = pURL + "&action=details";
						pURL = pURL + "&pagetype=front";
                  }	
		  
                break;
            }
            i++;
        }
        if (doNothing == false) {
            parent.frames['XcelAction'].location=pURL;
        }
    }
